<script type="text/javascript">function toasNotification(o,t){var i=t||"success";setTimeout(function(){notification(o,i)},100)}function notiInfo(o){toasNotification(o,"info")}function notiSuccess(o){toasNotification(o,"success")}function notiWarning(o){toasNotification(o,"warning")}function notiError(o){toasNotification(o,"error")}function notification(o,t){if(""==o)return!1;toastr.options={closeButton:!0,debug:!1,newestOnTop:!0,positionClass:"toast-top-center",progressBar:!1,showDuration:"300",hideDuration:"1000",timeOut:"5000",extendedTimeOut:"1000",showEasing:"swing",hideEasing:"linear",showMethod:"fadeIn",hideMethod:"fadeOut"},"info"==t?toastr.info(o):"success"==t?toastr.success(o):"warning"==t?toastr.warning(o):"error"==t&&toastr.error(o)}</script>